import React, { useState } from 'react';
import './History.css';

const pages = [
  {
    content: "PediaBright was founded by four innovative college students, who\n shared a common goal of supporting kid’s through their healthcare journey. Through extensive market research, PediaBright’s founders identified a root problem in pediatric healthcare",
  },
  {
    content: "The Problem: Communication barriers between pediatric patients and providers results in children with complex health conditions feeling confused & alone.",
  },
  {
    content: "The Solution: Improve the involvement of children with their own care by empowering guardians, caretakers, and physicians with a tool to share with their pediatric patients",
  }
];

function History() {
  const [currentIndex, setCurrentIndex] = useState(0);

  const goToPrevious = () => {
    setCurrentIndex((prevIndex) => (prevIndex === 0 ? pages.length - 1 : prevIndex - 1));
  };

  const goToNext = () => {
    setCurrentIndex((prevIndex) => (prevIndex === pages.length - 1 ? 0 : prevIndex + 1));
  };

  return (
    <div className="history-container">
      <div className="history-content">
        <p>{pages[currentIndex].content}</p>
        <div className="history-navigation">
          <button onClick={goToPrevious} className="arrow-left">&#8249;</button>
          <div className="history-circles">
            {pages.map((_, index) => (
              <span
                key={index}
                className={index === currentIndex ? 'circle active' : 'circle'}
              />
            ))}
          </div>
          <button onClick={goToNext} className="arrow-right">&#8250;</button>
        </div>
      </div>
    </div>
  );
}

export default History;
